import { loginAdmin } from "@apis/auth";
import "@assets/responsive/mobile.scss";
import Button from "@components/button";
import InputText from "@components/inputText";
import LogoLogin from "@components/logo-login";
import axios from "axios";
import { useForm } from "react-hook-form";

const LoginPage = () => {
  const { handleSubmit, register } = useForm();

  const onSubmit = async (data: any) => {
    try {
      const response = await axios.post("http://localhost:8080/api/v1/auth/admin/login", {
        username: data.username,
        password: data.password
      });
      
      console.log("Login successful:", response.data);
      // Xử lý đăng nhập thành công ở đây, có thể chuyển hướng người dùng hoặc thực hiện các hành động khác
      
    } catch (error) {
      console.error('Failed to login:', error);
      // Xử lý lỗi khi gọi API không thành công, có thể hiển thị thông báo lỗi cho người dùng
    }
  };

  return (
    <>
      <div className="title-login hidden">Welcome to ADMIN</div>
      <div className="container-login tablet w-[30%] h-[480px] bg-[#1A2226] mx-auto mt-[12%] flex items-center flex-col">
        <div className="logo mb-10">
          <LogoLogin></LogoLogin>
          <div className="text text-[#ECF0F5] font-semibold text-[30px] tracking-[2px]">
            ADMIN PANEL
          </div>
        </div>

        <form onSubmit={handleSubmit(onSubmit)} className="input w-full text-center">
          <InputText
            title="USERNAME"
            type="text"
            id="username"
            name="username"
            register={register}
          ></InputText>
          <InputText
            title="PASSWORD"
            type="password"
            id="password"
            name="password"
            register={register}
          ></InputText>
          <Button></Button>
        </form>
      </div>
    </>
  );
};

export default LoginPage;
