import { InputTextProps } from "@interfaces/InputText";
import { useState } from "react";

const InputText: React.FC<InputTextProps> = ({ title, type, id }) => {
  const [showPassword, setShowPassword] = useState(false);

  const handleCheckboxChange = () => {
    setShowPassword(!showPassword);
  };

  const inputType = type === 'password' && showPassword ? 'text' : type;

  return (
    <>
      <div className="mb-8">
        <label
          htmlFor={id}
          className="block form-text-label translate-x-[-190px]"
        >
          {title}
        </label>
        <input
          type={inputType}
          className="w-[80%] text-white bg-transparent border border-b-[#0DB8DE] border-transparent outline-none font-semibold p-1"
        />
      </div>
      {type === 'password' && (
        <div className="tr">
          <input
            type="checkbox"
            id="show-password"
            checked={showPassword}
            onChange={handleCheckboxChange}
          />
          <label htmlFor="show-password" className="ml-2 text-sm text-[#0DB8DE]">
            Show Password
          </label>
        </div>
      )}
    </>
  );
};

export default InputText;
