import { InputTextProps } from "@interfaces/InputText";
import { useState } from "react";

const InputText: React.FC<InputTextProps> = ({ title, type, id }) => {
  const [showPassword, setShowPassword] = useState(false);

  const toggleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const isPassword = type === 'password';
  const isCheckbox = type === 'checkbox';
  const inputType = isPassword && showPassword ? 'text' : type;

  return (
    <>
      <div className="mb-8">
        <label
          htmlFor={id}
          className="block form-text-label translate-x-[-190px]"
        >
          {title}
        </label>
        <input
          type={inputType}
          className="w-[80%] text-white bg-transparent border border-b-[#0DB8DE] border-transparent outline-none font-semibold p-1"
        />
      </div>
      {isPassword && (
        <button
          type="button"
          onClick={toggleShowPassword}
          className="text-sm text-[#0DB8DE] mt-2 translate-x-[-130px] translate-y-[-50px]"
        >
          {showPassword ? "Hide" : "Show"} Password
        </button>
      )}
    </>
  );
};

export default InputText;
